import React, { Component } from "react";
import "./App.css";

class App extends Component {
  state = {
    userInput: "",
    gifList: [],
    selectedGif: {}
  };

  //this is where we use API and retrieve data back
  componentDidMount() {
    fetch(
      "http://newsapi.org/v2/top-headlines?country=it&apiKey=38c6325b37ef4200b8d0b52b8e8ab90b"
    )
      .then(response => response.json())
      .then(gifResults => this.setState({ gifList: gifResults.articles }))
      .catch(error => console.error(`Oooops, no news today: ${error}`));
  }

  //this is where we have the changes to typing and click stored
  handleChange = e => this.setState({ userInput: e.target.value });
  handleUserInput = () => {
    let userInput = this.state.userInput ? this.state.userInput : "fox";

    fetch(
      "http://newsapi.org/v2/top-headlines?country=it&q={userInput}&apiKey=38c6325b37ef4200b8d0b52b8e8ab90b"
    )
      .then(response => response.json())
      .then(gifResults => this.setState({ gifList: gifResults.articles }))
      .catch(error => console.error(`Oooops, no news today: ${error}`));
  };

  //rendering data
  render() {
    return (
      <section className="App">
        <h1 className="App-header">Animals Who Read</h1>
        <div>
          <input type="text" onChange={this.handleChange} />
          <button type="submit" onClick={this.handleUserInput}>
            Which Fellow Animal would you like to learn about?
          </button>
        </div>
        ​
        {this.state.gifList.map(element => (
          <h2>so: {element.content}/> </h2>
        ))}
        ​
        {this.state.gifList.map(element => (
          <img key={element.id} src={element.urlToImage} alt="picture" />
        ))}
      </section>
    );
  }
}

export default App;
