import React from "react";

const Articles = props => (
  <div className="container">
    <div className="row">
      {props.articles.map(article => {
        return (
          <div
            key={article.title}
            className="col-md-6 col-sm-12 col-lg-4"
            style={{ marginBottom: "2rem" }}
          >
            <div className="article__box">
              <img
                className="article__box-img"
                src={article.urlToImage}
                alt={article.title}
              />
              <div className="article__text">
                <h5 className="articles__titles">{article.title} </h5>
                <p className="articles__subtitles">
                  Stinky Human Author: <span>{article.author}</span>
                </p>
              </div>
              <button className="article_buttons">View Article</button>
            </div>
          </div>
        );
      })}
    </div>
  </div>
);

export default Articles;
