import React, { Component } from "react";
import "./App.scss";
import Ticker from "./components/Ticker";
import "bootstrap/dist/css/bootstrap.min.css";
import Header from "./components/Header";
import Form from "./components/Form";

const API_KEY = "38c6325b37ef4200b8d0b52b8e8ab90b";

class App extends Component {
  // Making the API Call
  getArticles = async e => {
    const articleName = e.target.elements.articleName.value;
    e.preventDefault();
    const api_call = await fetch(
      `http://newsapi.org/v2/top-headlines?country=us&apiKey=${API_KEY}&pageSize=5`
    );

    const data = await api_call.json();

    console.log(data.articles[0].description);
  };

  render() {
    return (
      <div className="App">
        <Header />
        <Form getArticles={this.getArticles} />
      </div>
    );
  }
}

export default App;
